const numberStr = '';

const greatestProduct = (digits) => {
    let result = [];
    let max = 0;
    const numberSets = numberStr.split('').map(n => +n);
    const len = numberSets.length - digits + 1;

    // count each digit
    for (let i = 0; i < len; i++) {
        let sum = 0;
        const temp = [];
        for (let j = 0; j < digits; j++) {
            const target = numberSets[i + j];
            // ignore '0'
            if (target === 0) {
                sum = 0;
                break;
            }
            sum += target;
            temp.push(target);
        }

        if (sum > max) {
            max = sum;
            result = temp;
        }
    }

    return result;
}

const productArr = (result) => {
    return result.reduce((previousValue, currentValue) => previousValue * currentValue);
};

const result4 = greatestProduct(4);
console.log('4 adjacent digit => the greatest product are', result4, '=', productArr(result4));

const result13 = greatestProduct(13);
console.log('13 adjacent digit => the greatest product are', result13, '=', productArr(result13));

